public class HW1 {

	/**
	 * @param args
	 */
	
	//The following lines of code explains the different types of languages from one English sentence
	
	//I decided to do more than 2 languages to go the extra mile
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
			System.out.println("Hello from Java"); //Original Code
			System.out.println(); //line of space
			
			System.out.println("ellohay omfray avajay"); //Pig Latin
			System.out.println();
			
			System.out.println("marhabaan min jafa"); //Arabic
			System.out.println();
			
			System.out.println("Láizì Java de Hello"); //Traditional Chinese
			System.out.println();
			
			System.out.println("Privet iz Java"); //Russian
			System.out.println();
			
			System.out.println("Bonjour de Java"); //French
			System.out.println();
			
			System.out.println("Hallo von Java"); //German
			System.out.println();
			
			System.out.println("java sita Hello"); //Sinhala (Sri Lankan Language)
			System.out.println();
			
			System.out.println("Pozdrav iz Jave"); //Croatian
			System.out.println();
			
			System.out.println("Haro From javu~a"); //Japanese
			System.out.println();
			
			System.out.println("Hello Javalobuteo"); //Korean
			System.out.println();
			
			System.out.println("Olá De Java"); //Portuguese
			System.out.println();
			
			System.out.println("Hej från Java"); //Swedish
		}

	}

}
